import { Component } from '@angular/core';
import { FormControl, FormGroup, FormGroupName } from '@angular/forms';

@Component({
  selector: 'app-reactive-form',
  templateUrl: './reactive-form.component.html',
  styleUrls: ['./reactive-form.component.css']
})
export class ReactiveFormComponent {

  Reactive_Form_Content = new FormGroup({
    First_Name : new FormControl(),
    Second_Name : new FormControl(),
    address : new FormGroup({
      Area : new FormControl(),
      Mobile : new FormControl()
    })
  })

  Reactive_Form_Summit()
  {
    console.log(this.Reactive_Form_Content.value);

  }
}
<div class="container mt-2">
    <form [formGroup]="Reactive_Form_Content" (submit)="Reactive_Form_Summit()">
        <div class="row">
            <div class="col-3"><label>First Name</label></div>
            <div class="col-3"><input type="text" formControlName="First_Name" ngModel></div>
            <div class="col-3"><label>Second Name</label></div>
            <div class="col-3"><input type="text" formControlName="Second_Name" ngModel></div>
        </div>
        <div class="row">
            <button type="submit">Submit Reactive</button>
        </div>
    </form>
</div>
