import { Component,OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-forms',
  templateUrl: './forms.component.html',
  styleUrls: ['./forms.component.css']
})
export class FormsComponent implements OnInit {

  First_Name  = "";
  Last_Name   = "";
  ngOnInit(): void {
    this.First_Name = "Keerthi"  
    this.Last_Name  = "Kumar"
  }

  Form_Submit(form:NgForm)
  {
    console.log(form);

  }
}


<div class="container border">
    <form #constactForm="ngForm" (submit)="Form_Submit(constactForm)">
        <div class="row">
            <div class="col-2">First Name</div>
            <div class="col-3"><input [(ngModel)]="First_Name"></div>
            <div class="col-2">Last Name</div>
            <div class="col-3"><input [(ngModel)]="Last_Name"></div>
        </div>
        <div class="row">
            <button type="submit">Submit</button>
        </div>

    </form>
</div>
