import { Component } from '@angular/core';
import { FormControl, FormGroup,Validator, FormGroupName, Validators } from '@angular/forms';

@Component({
  selector: 'app-reactive-form',
  templateUrl: './reactive-form.component.html',
  styleUrls: ['./reactive-form.component.css']
})
export class ReactiveFormComponent {

  Reactive_Form_Content = new FormGroup({
    First_Name : new FormControl('',[Validators.required,Validators.minLength(5)]),
    Second_Name : new FormControl('Kumar'),
    address : new FormGroup({
      Area : new FormControl('kulathur',[Validators.required,Validators.maxLength(5)]),
      Mobile : new FormControl('94869558074')
    })
  })

  Reactive_Form_Summit()
  {
    console.log(this.Reactive_Form_Content.value);

  }


}
<div class="container mt-2">
    <form [formGroup]="Reactive_Form_Content" (submit)="Reactive_Form_Summit()">
        <div class="row">
            <div class="col-3"><label>First Name</label></div>
            <div class="col-3"><input type="text" formControlName="First_Name">
                <span *ngIf="((!Reactive_Form_Content.controls.First_Name.valid) && (Reactive_Form_Content.controls.First_Name.touched))">Fill The Test Fileld</span>
            </div>
            <div class="col-3"><label>Second Name</label></div>
            <div class="col-3"><input type="text" formControlName="Second_Name"></div>
        </div>
        <div class="row" formGroupName="address">
            <div class="col-3">Area</div>
            <span *ngIf="((!Reactive_Form_Content.controls.address.controls.Area.valid) && (Reactive_Form_Content.controls.address.controls.Area.touched))">
                <span *ngIf="((Reactive_Form_Content.controls.address.controls.Area.valid))">Fill the Text</span>
                <span *ngIf="(Reactive_Form_Content.controls.address.controls.Area.errors?.['minlength'])">Above filve Letter</span>
            </span>
            <div class="col-3"><input type="text" formControlName="Area"></div>
            <div class="col-3">Mobile</div>
            <div class="col-3"><input type="text" formControlName="Mobile"></div>
        </div>
        <div class="row">
            <button type="submit">Submit Reactive</button>
        </div>
    </form>
</div>
